import React,{Component} from 'react';
import Web3 from 'web3'
import './App.css';
import {TODO_LIST_ADDRESS, TODO_LIST_ABI} from './config'

class App extends Component{

  componentWillMount(){
    this.loadBlockchainData()
  }

  async loadBlockchainData(){
    const web3 = new Web3(new Web3.providers.HttpProvider("http://localhost:7545"))
    const accounts = await web3.eth.getAccounts()
    console.log(accounts[0])
    this.setState({ account: accounts[0]})
    const todoList = new web3.eth.Contract(TODO_LIST_ABI, TODO_LIST_ADDRESS)
    this.setState({todoList})
    console.log("TodoList: ",todoList)
    const taskCount = await todoList.methods.taskCount().call()
//    this.setState({ taskCount })
    console.log(taskCount.toNumber())
  }

  constructor(props){
    super(props)
    this.state = {
      account: '',
      taskCount: 0
    }
  }

  render(){
  return (

    <div className = "container">
       <div>
    <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
      <a className="navbar-brand col-sm-3 col-md-2 mr-0" href="http://www.dappuniversity.com/free-download" target="_blank">Dapp University | Todo List</a>
      <ul className="navbar-nav px-3">
        <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
          <small><a className="nav-link" href="#"><span id="account"></span></a></small>
        </li>
      </ul>
    </nav>
    <div className="container-fluid">
      <div className="row">

      </div>
    </div>
  </div>
    </div>
  );
  }
}

export default App;
